setup.pg-code:
$thirtyNine = Real(39);
$OneFortyOne = Real(141);
$FortyFour = Real(44);
$Increase = PopUp(["?", "Increase", "Decrease", "None of the above", "All of the above"], "Increase");
$Decrease = PopUp(["?", "Increase", "Decrease", "None of the above", "All of the above"], "Decrease");

      Context()->variables->are(t=>'Real');
      $a=random(200,400,10);
      $r=random(0.05,0.09,0.01);
      $p=$r*100;
      $b=1+$r;
      @x=(0,1,2,3,4,20,Formula("t"));
    Context()->flags->set(formatStudentAnswers=>'parsed');
    Context()->flags->set(reduceConstants=>0);
    Context()->operators->set(
    '*' => {class => 'bizarro::BOP::multiply', isCommand => 1},
    ' *' => {class => 'bizarro::BOP::multiply', isCommand => 1},
    '* ' => {class => 'bizarro::BOP::multiply', isCommand => 1},
    '**' => {class => 'bizarro::BOP::power', isCommand => 1, perl=>undef},
    '^' => {class => 'bizarro::BOP::power', isCommand => 1, perl=>undef},
    '^' => {class => 'bizarro::BOP::power', isCommand => 1, perl=>undef},
    '^' => {class => 'bizarro::BOP::power', isCommand => 1, perl=>undef},
    );
      @y=($a,
            $a.'\cdot'.$b,
            $a.'\cdot'.$b.'\cdot'.$b,
            Formula("$a*$b*$b*$b"),
            Formula("$a*$b*$b*$b*$b"),
            Formula("$a*$b^20"),
            Formula("$a*$b^t"),);

            for my$i(3..6){$ev[$i]=$y[$i]->cmp(
   checker=>sub{
      my ( $c, $s, $ansHash ) = @_;
      return 0 if $ansHash->{isPreview} || $c != $s;
      $s = $ansHash->{student_formula};
      $c = $c->{original_formula} if defined $c->{original_formula};
      $s = Formula("$s"); $c = Formula("$c");
      return 0 unless ($c == $s);
      Context()->flags->set(bizarroMul=>1,bizarroPow=>1);
      delete $c->{test_values}, $s->{test_values};
      Value->Error("Your answer is correct, but write it out to see the pattern forming.") unless (($c == $s) or ($s == $c));
      Context()->flags->set(bizarroMul=>0,bizarroPow=>0);
      return 1;
});}
