setup.pg-code:
Context()->operators->set(
    '**' => {class => 'bizarro::BOP::power', isCommand => 1, perl=>undef},
    '^' => {class => 'bizarro::BOP::power', isCommand => 1, perl=>undef},
);
$b = random(2,5,1);
$x = random(2,5,1);
do{$y=random(2,5,1)} until($x!=$y);
if($envir{problemSeed}==1){$b=2;$x=3;$y=4;};
$z=$x+$y;
Context()->flags->set(reduceConstants=>0);
$N=Formula("$b^$z");
$evaluator=$N->cmp(checker=>sub{
    my($c,$s,$aH)=@_;
    return 0 if $aH->{isPreview}||$c!=$s;
    $s=$aH->{student_formula};
    $c=$c->{original_formula} if defined $c->{original_formula};
    $s= Formula("$s");$c=Formula("$c");
    return 0 unless ($c==$s);
    Context()->flags->set(bizarroPow=>1);
    delete $c->{test_values}, $s->{test_values};
    my $OK = (($c==$s) or ($s==$c));
    Context()->flags->set(bizarroPow=>0);
    Value::Error("This is the right number, but right now you are being asked to write it in a certain exponential form.") unless $OK;
    return $OK;
});
Context("LimitedNumeric");
$bx=Real($b**$x);
$by=Real($b**$y);
$bz=Real($b**$z);
