\begin{tikzpicture}
  \begin{axis}[
      axis line style={->},
      xmin={0},
      xmax={15},
      ymin={0},
      ymax={1.3},
      x label style={at={(axis description cs:0.5,-0.1)},anchor=north},
      xlabel={$t$ (hours)},
      ylabel={$D(t)$ (mg of drug)},
      xtick={},
      ytick={},
      minor xtick={},
      minor ytick={}]
    \addplot+[->,domain=0:15] {0.5^(x/3)};
    \addplot[guideline,domain=0:15] {0.1};
    \addplot[mark=*] coordinates {(9.97, 0.1)} node[above right] {$t\approx10$};
  \end{axis}
\end{tikzpicture}

