    1, labels => ["f(radius)=area","f(area)=radius","None of these"],
    displayLabels => 0
);
$independentVariable = RadioButtons(
    ["Radius","Area", "None of these"],
    1, labels => ["Radius","Area","None of these"],
    displayLabels => 0
);
