    ["\(5.64\text{ ft}\)", "\(5.64\text{ ft}^2\)", "\(5.64\text{ ft}^3\)"],
    0, labels => ["5.64 ft", "5.64 ft^2", "5.64 ft^3"],
    displayLabels => 0
);

